import React, { useState } from 'react';
import './App.css';

function App() {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    email: '',
    statement: ''
  });

  // Handler for form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handler for form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data Submitted:', formData);
    setShowForm(false);
    // You would typically send formData to the backend here
  };

  return (
    <div className="App">
      <header className="header">
        <h1>Proviz School of AI</h1>
        <p>Empowering the next generation of AI leaders</p>
        <button className="apply-button" onClick={() => setShowForm(true)}>
          Apply Now
        </button>
      </header>

      <section className="intro">
        <h2>Welcome to Proviz School of AI</h2>
        <p>
          At Proviz School of AI, our mission is to educate and inspire future AI professionals.
          We offer cutting-edge courses and hands-on projects to equip you with the skills needed
          in the fast-evolving field of artificial intelligence.
        </p>
      </section>

      {/* Optional: Additional sections like Course Highlights, Testimonials, etc. */}
      <section className="highlights">
        <h3>Why Choose Us?</h3>
        <ul>
          <li>Comprehensive AI Curriculum</li>
          <li>Experienced Instructors</li>
          <li>Hands-on Projects and Real-World Applications</li>
        </ul>
      </section>

      {/* Application Form Pop-Up */}
      {showForm && (
        <div className="form-popup">
          <div className="form-container">
            <h2>Application Form</h2>
            <form onSubmit={handleSubmit}>
              <label>
                Name:
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </label>
              <label>
                Phone:
                <input
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  required
                />
              </label>
              <label>
                Email:
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </label>
              <label>
                Brief Statement:
                <textarea
                  name="statement"
                  value={formData.statement}
                  onChange={handleChange}
                  required
                />
              </label>
              <button type="submit">Submit</button>
              <button type="button" onClick={() => setShowForm(false)}>
                Close
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
